i=0;for action in allume éteint; do for lum in "la lumière" "la lampe" "l ampoule"; do for destination in "la cuisine" "du salon" "de la salle a manger"; do echo "<gram$i>" = marvin $action $lum $destination";"; i=$(($i+1));done; done; done > grammar.jsgf.tmp ;(echo -e "#JSGF V1.0 latin-1;\ngrammar robot;\npublic <question> = \c";i=$(($i-0));for j in $(seq 0 $i); do echo -e "<gram$j> \c"; [[ $j -gt 0 ]] && [[ $j -lt $i ]] && echo -e "| \c"; [[ $j -eq $i ]] && echo ";"; done) > grammar.jsgf;cat grammar.jsgf.tmp >> grammar.jsgf;echo "<gram$i> = marvin comment ca va;" >> grammar.jsgf
